package Day9.String;

public class replace_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String str1 = "Hello";
		String str2 = str1.replace('l', 'w'); // this will replace all the old character in this new character
		System.out.println(str2);    // It will change all the l into w
	}

}
