package Day9.String;

public class toUpper_toLowercase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String st = "hello";
		System.out.println(st.toUpperCase()); // when we use toUpperCase it changes the string into upper case
		
		
		String st1 = "HELLO";
		System.out.println(st1.toLowerCase()); // toLowerCase changes the upper case into lower case
	}

}
