package Day9.String;

public class trim_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// we use trim to eliminate unnecessary spaces or characters in the string
		
		String str1 = "    Hello world         ";
		System.out.println(str1.trim()); // when we use trim it eliminate the useless spaces and print out Hello World
	}

}
